package frontend;import javafx.fxml.FXML;import javafx.scene.control.*;import java.util.ResourceBundle;import static frontend.LoginController.lang;public class HelpController {    @FXML    private Label help;    @FXML    void initialize() {        switch (lang) {            case "LIETUVIŠKAS" -> {                ResourceBundle labels = ResourceBundle.getBundle("login_button_lt");                String buttonText = labels.getString("help");                help.setText(buttonText);                lang = "LIETUVIŠKAS";            }            case "ENGLISH" -> {                ResourceBundle labels = ResourceBundle.getBundle("login_button_en");                String buttonText = labels.getString("help");                help.setText(buttonText);                lang = "ENGLISH";            }            case "Islandų" -> {                ResourceBundle labels = ResourceBundle.getBundle("login_button_is");                String buttonText = labels.getString("help");                help.setText(buttonText);                lang = "Islandų";            }            case "RUSSIAN" -> {                help.setText("""                        add {element} : добавить новый элемент в коллекцию                        update id {element} : обновить значение элемента коллекции, id которого равен заданному                        remove_by_id id : удалить элемент из коллекции по его id                        execute_script file_name : считать и исполнить скрипт из указанного файла. В скрипте содержатся команды в таком же виде, в котором их вводит пользователь в интерактивном режиме.                        insert_at index {element} : добавить новый элемент в заданную позицию                        remove_any_by_oscars_count oscarsCount : удалить из коллекции один элемент, значение поля oscarsCount которого эквивалентно заданному                        average_of_oscars_count : вывести среднее значение поля oscarsCount для всех элементов коллекции                        print_field_ascending_genre : вывести значения поля genre всех элементов в порядке возрастания                                 """);                lang = "RUSSIAN";            }        }    }}